pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./interfaces/IAIOracle.sol";

contract PredictionMarket is ReentrancyGuard, Pausable {
    enum MarketStatus { Active, Resolved, Disputed, Cancelled }
    enum OracleType { AI, Manual, Hybrid }

    struct Bet {
        uint256 amount;
        uint256 outcome;
        uint256 timestamp;
        bool claimed;
    }

    struct Outcome {
        string name;
        uint256 totalStaked;
        uint256 betCount;
    }

    // State variables
    string public title;
    string public description;
    Outcome[] public outcomes;
    uint256 public expiryTime;
    address public creator;
    address public factory;
    address public oracleAddress;
    OracleType public oracleType;
    MarketStatus public status;
    
    uint256 public totalVolume;
    uint256 public winningOutcome;
    uint256 public resolutionTime;
    uint256 public platformFeeRate; // Basis points (e.g., 250 = 2.5%)
    
    mapping(address => Bet[]) public userBets;
    mapping(address => uint256) public userTotalStaked;
    mapping(uint256 => uint256) public outcomeStaked;
    
    address[] public participants;
    mapping(address => bool) public hasParticipated;

    // Events
    event BetPlaced(
        address indexed user,
        uint256 indexed outcome,
        uint256 amount,
        uint256 timestamp
    );
    
    event MarketResolved(
        uint256 indexed winningOutcome,
        uint256 timestamp,
        string resolution
    );
    
    event RewardClaimed(
        address indexed user,
        uint256 amount,
        uint256 timestamp
    );
    
    event MarketDisputed(
        address indexed disputer,
        uint256 timestamp,
        string reason
    );

    event MarketCancelled(uint256 timestamp, string reason);

    modifier onlyCreator() {
        require(msg.sender == creator, "Only market creator allowed");
        _;
    }

    modifier onlyFactory() {
        require(msg.sender == factory, "Only factory allowed");
        _;
    }

    modifier onlyOracle() {
        require(msg.sender == oracleAddress, "Only oracle allowed");
        _;
    }

    modifier marketActive() {
        require(status == MarketStatus.Active, "Market not active");
        require(block.timestamp < expiryTime, "Market expired");
        _;
    }

    modifier marketResolved() {
        require(status == MarketStatus.Resolved, "Market not resolved");
        _;
    }

    constructor(
        string memory _title,
        string memory _description,
        string[] memory _outcomes,
        uint256 _expiryTime,
        address _creator,
        address _oracleAddress,
        uint8 _oracleType,
        address _factory
    ) {
        require(_outcomes.length >= 2, "Need at least 2 outcomes");
        require(_expiryTime > block.timestamp, "Expiry must be in future");
        
        title = _title;
        description = _description;
        expiryTime = _expiryTime;
        creator = _creator;
        oracleAddress = _oracleAddress;
        oracleType = OracleType(_oracleType);
        factory = _factory;
        status = MarketStatus.Active;
        platformFeeRate = 250; // 2.5% default fee
        
        // Initialize outcomes
        for (uint256 i = 0; i < _outcomes.length; i++) {
            outcomes.push(Outcome({
                name: _outcomes[i],
                totalStaked: 0,
                betCount: 0
            }));
        }
    }

    /**
     * @dev Place a bet on a specific outcome
     * @param _outcome Index of the outcome to bet on
     */
    function placeBet(uint256 _outcome) 
        external 
        payable 
        nonReentrant 
        marketActive 
        whenNotPaused 
    {
        require(_outcome < outcomes.length, "Invalid outcome");
        require(msg.value > 0, "Bet amount must be positive");
        
        // Record bet
        userBets[msg.sender].push(Bet({
            amount: msg.value,
            outcome: _outcome,
            timestamp: block.timestamp,
            claimed: false
        }));

        // Update tracking variables
        outcomes[_outcome].totalStaked += msg.value;
        outcomes[_outcome].betCount++;
        outcomeStaked[_outcome] += msg.value;
        userTotalStaked[msg.sender] += msg.value;
        totalVolume += msg.value;

        // Track unique participants
        if (!hasParticipated[msg.sender]) {
            participants.push(msg.sender);
            hasParticipated[msg.sender] = true;
        }

        emit BetPlaced(msg.sender, _outcome, msg.value, block.timestamp);
    }

    /**
     * @dev Resolve market with winning outcome
     * @param _winningOutcome Index of the winning outcome
     * @param _resolution Description of resolution
     */
    function resolveMarket(uint256 _winningOutcome, string memory _resolution) 
        external 
        onlyOracle 
        nonReentrant 
    {
        require(status == MarketStatus.Active, "Market not active");
        require(block.timestamp >= expiryTime, "Market not expired yet");
        require(_winningOutcome < outcomes.length, "Invalid winning outcome");
        
        status = MarketStatus.Resolved;
        winningOutcome = _winningOutcome;
        resolutionTime = block.timestamp;
        
        emit MarketResolved(_winningOutcome, block.timestamp, _resolution);
    }

    /**
     * @dev Claim rewards for winning bets
     */
    function claimReward() external nonReentrant marketResolved {
        require(userBets[msg.sender].length > 0, "No bets found");
        
        uint256 totalReward = calculateUserReward(msg.sender);
        require(totalReward > 0, "No rewards to claim");
        
        // Mark bets as claimed
        for (uint256 i = 0; i < userBets[msg.sender].length; i++) {
            if (userBets[msg.sender][i].outcome == winningOutcome && 
                !userBets[msg.sender][i].claimed) {
                userBets[msg.sender][i].claimed = true;
            }
        }
        
        // Calculate platform fee
        uint256 platformFee = (totalReward * platformFeeRate) / 10000;
        uint256 userReward = totalReward - platformFee;
        
        // Transfer rewards
        (bool success, ) = payable(msg.sender).call{value: userReward}("");
        require(success, "Reward transfer failed");
        
        // Transfer platform fee to factory
        if (platformFee > 0) {
            (bool feeSuccess, ) = payable(factory).call{value: platformFee}("");
            require(feeSuccess, "Fee transfer failed");
        }
        
        emit RewardClaimed(msg.sender, userReward, block.timestamp);
    }

    /**
     * @dev Calculate potential reward for a user
     */
    function calculateUserReward(address _user) public view returns (uint256) {
        if (status != MarketStatus.Resolved) return 0;
        
        uint256 userWinningStake = 0;
        
        // Calculate user's winning stakes
        for (uint256 i = 0; i < userBets[_user].length; i++) {
            Bet memory bet = userBets[_user][i];
            if (bet.outcome == winningOutcome && !bet.claimed) {
                userWinningStake += bet.amount;
            }
        }
        
        if (userWinningStake == 0) return 0;
        
        uint256 totalWinningStake = outcomes[winningOutcome].totalStaked;
        if (totalWinningStake == 0) return 0;
        
        // Calculate proportional share of total pool
        return (userWinningStake * totalVolume) / totalWinningStake;
    }

    /**
     * @dev Get market information
     */
    function getMarketInfo() external view returns (
        string memory _title,
        string memory _description,
        uint256 _totalVolume,
        uint256 _expiryTime,
        MarketStatus _status,
        uint256 _participantCount,
        uint256 _outcomeCount
    ) {
        return (
            title,
            description,
            totalVolume,
            expiryTime,
            status,
            participants.length,
            outcomes.length
        );
    }

    /**
     * @dev Get all outcomes
     */
    function getOutcomes() external view returns (Outcome[] memory) {
        return outcomes;
    }

    /**
     * @dev Get user's bets
     */
    function getUserBets(address _user) external view returns (Bet[] memory) {
        return userBets[_user];
    }

    /**
     * @dev Get market odds for each outcome
     */
    function getOdds() external view returns (uint256[] memory) {
        uint256[] memory odds = new uint256[](outcomes.length);
        
        if (totalVolume == 0) {
            // Equal odds if no bets placed
            for (uint256 i = 0; i < outcomes.length; i++) {
                odds[i] = 10000 / outcomes.length; // Basis points
            }
        } else {
            for (uint256 i = 0; i < outcomes.length; i++) {
                odds[i] = (outcomes[i].totalStaked * 10000) / totalVolume;
            }
        }
        
        return odds;
    }

    /**
     * @dev Emergency pause (only creator or factory)
     */
    function pause() external {
        require(msg.sender == creator || msg.sender == factory, "Unauthorized");
        _pause();
    }

    /**
     * @dev Unpause (only creator or factory)
     */
    function unpause() external {
        require(msg.sender == creator || msg.sender == factory, "Unauthorized");
        _unpause();
    }

    /**
     * @dev Cancel market and refund all participants
     */
    function cancelMarket(string memory _reason) external onlyFactory {
        require(status == MarketStatus.Active, "Market not active");
        
        status = MarketStatus.Cancelled;
        
        // Refund all participants
        for (uint256 i = 0; i < participants.length; i++) {
            address participant = participants[i];
            uint256 totalStaked = userTotalStaked[participant];
            
            if (totalStaked > 0) {
                userTotalStaked[participant] = 0;
                (bool success, ) = payable(participant).call{value: totalStaked}("");
                require(success, "Refund failed");
            }
        }
        
        emit MarketCancelled(block.timestamp, _reason);
    }

    /**
     * @dev Dispute market resolution
     */
    function disputeMarket(string memory _reason) external payable {
        require(status == MarketStatus.Resolved, "Market not resolved");
        require(block.timestamp <= resolutionTime + 7 days, "Dispute period expired");
        require(msg.value >= 0.1 ether, "Insufficient dispute bond");
        
        status = MarketStatus.Disputed;
        
        emit MarketDisputed(msg.sender, block.timestamp, _reason);
    }

    /**
     * @dev Get participants list
     */
    function getParticipants() external view returns (address[] memory) {
        return participants;
    }

    /**
     * @dev Check if user has unclaimed rewards
     */
    function hasUnclaimedRewards(address _user) external view returns (bool) {
        if (status != MarketStatus.Resolved) return false;
        return calculateUserReward(_user) > 0;
    }
}","keccak256":"0xde68b8254bb00048432be4040aa2dcdb1451b87a2d425e883ac1c1317c51f930","license":"MIT"},"contracts/StakingContract.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract StakingContract is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    struct StakeInfo {
        uint256 amount;
        uint256 timestamp;
        uint256 lockPeriod; // in seconds
        uint256 rewardDebt;
        bool isValidator;
    }

    struct Validator {
        address validatorAddress;
        uint256 totalStaked;
        uint256 reputation; // 0-1000
        uint256 successfulResolutions;
        uint256 totalResolutions;
        bool isActive;
        uint256 slashCount;
    }

    // State variables
    IERC20 public orxToken;
    
    mapping(address => StakeInfo) public stakes;
    mapping(address => Validator) public validators;
    address[] public validatorList;
    
    uint256 public totalStaked;
    uint256 public rewardRate; // Reward per second per token (scaled by 1e18)
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public minimumValidatorStake;
    uint256 public minimumStakingPeriod;
    uint256 public slashingRate; // Percentage to slash (basis points)
    
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;

    // Events
    event TokensStaked(address indexed user, uint256 amount, uint256 lockPeriod, bool isValidator);
    event TokensUnstaked(address indexed user, uint256 amount);
    event RewardClaimed(address indexed user, uint256 reward);
    event ValidatorRegistered(address indexed validator, uint256 stakedAmount);
    event ValidatorSlashed(address indexed validator, uint256 slashedAmount, string reason);
    event RewardsDistributed(uint256 totalRewards, uint256 rewardRate);

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;

        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    modifier validatorOnly() {
        require(validators[msg.sender].isActive, "Not an active validator");
        _;
    }

    constructor(
        address _orxToken,
        uint256 _rewardRate,
        uint256 _minimumValidatorStake,
        uint256 _minimumStakingPeriod,
        uint256 _slashingRate
    ) Ownable(msg.sender) {
        orxToken = IERC20(_orxToken);
        rewardRate = _rewardRate;
        minimumValidatorStake = _minimumValidatorStake;
        minimumStakingPeriod = _minimumStakingPeriod;
        slashingRate = _slashingRate;
        lastUpdateTime = block.timestamp;
    }

    /**
     * @dev Stake ORX tokens
     * @param _amount Amount of tokens to stake
     * @param _lockPeriod Lock period in seconds
     * @param _asValidator Whether to register as validator
     */
    function stakeTokens(
        uint256 _amount,
        uint256 _lockPeriod,
        bool _asValidator
    ) external nonReentrant updateReward(msg.sender) {
        require(_amount > 0, "Cannot stake 0 tokens");
        require(_lockPeriod >= minimumStakingPeriod, "Lock period too short");
        
        if (_asValidator) {
            require(_amount >= minimumValidatorStake, "Insufficient stake for validator");
            require(!validators[msg.sender].isActive, "Already a validator");
        }

        // Transfer tokens from user
        orxToken.safeTransferFrom(msg.sender, address(this), _amount);

        // Update stake info
        StakeInfo storage stake = stakes[msg.sender];
        stake.amount += _amount;
        stake.timestamp = block.timestamp;
        stake.lockPeriod = _lockPeriod;
        stake.isValidator = _asValidator;

        totalStaked += _amount;

        // Register as validator if requested
        if (_asValidator) {
            _registerValidator(msg.sender, _amount);
        }

        emit TokensStaked(msg.sender, _amount, _lockPeriod, _asValidator);
    }

    /**
     * @dev Unstake tokens
     * @param _amount Amount to unstake
     */
    function unstakeTokens(uint256 _amount) 
        external 
        nonReentrant 
        updateReward(msg.sender) 
    {
        StakeInfo storage stake = stakes[msg.sender];
        require(stake.amount >= _amount, "Insufficient staked amount");
        require(
            block.timestamp >= stake.timestamp + stake.lockPeriod,
            "Tokens still locked"
        );

        // If validator, check if they can unstake
        if (stake.isValidator && validators[msg.sender].isActive) {
            require(
                stake.amount - _amount >= minimumValidatorStake || _amount == stake.amount,
                "Would fall below minimum validator stake"
            );
            
            if (_amount == stake.amount) {
                // Remove validator status
                validators[msg.sender].isActive = false;
                _removeFromValidatorList(msg.sender);
            }
        }

        // Update stake
        stake.amount -= _amount;
        totalStaked -= _amount;

        if (stake.amount == 0) {
            delete stakes[msg.sender];
        }

        // Transfer tokens back to user
        orxToken.safeTransfer(msg.sender, _amount);

        emit TokensUnstaked(msg.sender, _amount);
    }

    /**
     * @dev Claim staking rewards
     */
    function claimRewards() external nonReentrant updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        require(reward > 0, "No rewards to claim");

        rewards[msg.sender] = 0;
        orxToken.safeTransfer(msg.sender, reward);

        emit RewardClaimed(msg.sender, reward);
    }

    /**
     * @dev Calculate current reward per token
     */
    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored +
            (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) / totalStaked);
    }

    /**
     * @dev Calculate earned rewards for an account
     */
    function earned(address account) public view returns (uint256) {
        return
            ((stakes[account].amount *
                (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18) +
            rewards[account];
    }

    /**
     * @dev Register as validator
     */
    function _registerValidator(address _validator, uint256 _stakedAmount) internal {
        validators[_validator] = Validator({
            validatorAddress: _validator,
            totalStaked: _stakedAmount,
            reputation: 500, // Start with medium reputation
            successfulResolutions: 0,
            totalResolutions: 0,
            isActive: true,
            slashCount: 0
        });

        validatorList.push(_validator);

        emit ValidatorRegistered(_validator, _stakedAmount);
    }

    /**
     * @dev Remove validator from list
     */
    function _removeFromValidatorList(address _validator) internal {
        for (uint256 i = 0; i < validatorList.length; i++) {
            if (validatorList[i] == _validator) {
                validatorList[i] = validatorList[validatorList.length - 1];
                validatorList.pop();
                break;
            }
        }
    }

    /**
     * @dev Slash validator stake for malicious behavior
     */
    function slashStake(address _validator, string memory _reason) 
        external 
        onlyOwner 
        updateReward(_validator) 
    {
        require(validators[_validator].isActive, "Validator not active");
        
        StakeInfo storage stake = stakes[_validator];
        uint256 slashAmount = (stake.amount * slashingRate) / 10000;
        
        if (slashAmount > stake.amount) {
            slashAmount = stake.amount;
        }

        // Update stake
        stake.amount -= slashAmount;
        totalStaked -= slashAmount;

        // Update validator info
        validators[_validator].slashCount++;
        validators[_validator].reputation = validators[_validator].reputation > 100 ? 
            validators[_validator].reputation - 100 : 0;

        // If stake falls below minimum, deactivate validator
        if (stake.amount < minimumValidatorStake) {
            validators[_validator].isActive = false;
            _removeFromValidatorList(_validator);
        }

        // Burn slashed tokens (send to dead address)
        orxToken.safeTransfer(address(0x000000000000000000000000000000000000dEaD), slashAmount);

        emit ValidatorSlashed(_validator, slashAmount, _reason);
    }

    /**
     * @dev Distribute rewards to stakers
     */
    function distributeRewards(uint256 _rewardAmount) 
        external 
        onlyOwner 
        updateReward(address(0)) 
    {
        require(_rewardAmount > 0, "No rewards to distribute");
        
        // Transfer reward tokens to contract
        orxToken.safeTransferFrom(msg.sender, address(this), _rewardAmount);

        emit RewardsDistributed(_rewardAmount, rewardRate);
    }

    /**
     * @dev Update validator performance
     */
    function updateValidatorPerformance(
        address _validator,
        bool _successful
    ) external onlyOwner {
        require(validators[_validator].isActive, "Validator not active");
        
        Validator storage validator = validators[_validator];
        validator.totalResolutions++;
        
        if (_successful) {
            validator.successfulResolutions++;
            // Increase reputation for successful resolution
            if (validator.reputation < 1000) {
                validator.reputation += 10;
            }
        } else {
            // Decrease reputation for failed resolution
            if (validator.reputation > 10) {
                validator.reputation -= 10;
            }
        }
    }

    /**
     * @dev Get validator list
     */
    function getValidators() external view returns (address[] memory) {
        return validatorList;
    }

    /**
     * @dev Get active validators only
     */
    function getActiveValidators() external view returns (address[] memory) {
        uint256 activeCount = 0;
        
        // Count active validators
        for (uint256 i = 0; i < validatorList.length; i++) {
            if (validators[validatorList[i]].isActive) {
                activeCount++;
            }
        }

        // Build active validators array
        address[] memory activeValidators = new address[](activeCount);
        uint256 index = 0;
        
        for (uint256 i = 0; i < validatorList.length; i++) {
            if (validators[validatorList[i]].isActive) {
                activeValidators[index] = validatorList[i];
                index++;
            }
        }

        return activeValidators;
    }

    /**
     * @dev Update contract parameters
     */
    function updateParameters(
        uint256 _rewardRate,
        uint256 _minimumValidatorStake,
        uint256 _minimumStakingPeriod,
        uint256 _slashingRate
    ) external onlyOwner {
        rewardRate = _rewardRate;
        minimumValidatorStake = _minimumValidatorStake;
        minimumStakingPeriod = _minimumStakingPeriod;
        slashingRate = _slashingRate;
    }

    /**
     * @dev Get stake info for user
     */
    function getStakeInfo(address _user) external view returns (StakeInfo memory) {
        return stakes[_user];
    }

    /**
     * @dev Get validator info
     */
    function getValidatorInfo(address _validator) external view returns (Validator memory) {
        return validators[_validator];
    }

    /**
     * @dev Check if address is active validator
     */
    function isActiveValidator(address _validator) external view returns (bool) {
        return validators[_validator].isActive;
    }
}","keccak256":"0xd064ef1cb7984ca5d60ff499cb3358e69073af9640116a0dc1357699b5cea883","license":"MIT"},"contracts/interfaces/IAIOracle.sol":{"content":"// SPDX-License-Identifier: MIT
