interface IAIOracle {
    function submitAIResult(
        address _market,
        uint256 _predictedOutcome,
        uint256 _confidence,
        string memory _evidence
    ) external;

    function getAIResult(address _market) 
        external 
        view 
        returns (uint256 outcome, uint256 confidence, bool isSubmitted);
}","keccak256":"0x1de79fcc4243ee5adf4b4930b641ef47b9e3c6cc0f2c88c24b73b4e65f5bbf86","license":"MIT"}},"version":1}
